var HttpError = require( './httperror' );

// function to authenticate and authorize a request 
// (only called on lease modifications)
// Function is imported as the 'auth_request' method for each request. 
// cb should be called with a result with a pseudo-boolean:
// - On an error: pass a HttpError object with the proper response code and message
// - On success: pass a null value
var auth = function( cb ) {
  // Trivial implementation, everybody gets auth'ed
  cb( null );
};

// Example of how to write an auth method
// var auth = function( cb ) {
  // Access attributes of the request
  // this.namespace == the bit of the request url before the primitive keyword
  
  // Return an authorization http error
  // cb( HttpError( 501, 'Not authorized' ));
  
  // Return a successful auth operation
  // cb( null );
// };


module.exports = auth;